import React, {Component} from "react";
import styled from "styled-components";
import { HobbyCard } from "../../share-components/Cards/HobbyCard";



class Hobby extends React.Component {

    componentDidMount=()=>{
        var w = window.innerWidth;

        console.log("test: "+w);

        if(w>1000)
            return true;
        else 
            return false;
    }

    render(){
    return(
        <Wrapper>
        {/* <HobbyContainer> */}
            <HobbyCard Icon={"fas fa-music fa-3x"} isHover={this.findWindowSize} name={"Music"} description={"I enjoy listening and playing music instrument"}/>
            <HobbyCard Icon={"fas fa-gamepad fa-3x"} isHover={this.findWindowSize} name={"Video Game"} description={"A gamer when I have free time"}/>
            <HobbyCard Icon={"fas fa-dumbbell fa-3x"} isHover={this.findWindowSize} name={"Weightlifting"} description={"Weightlifting allow me to improve and challenge myself"}/>
            <HobbyCard Icon={"fas fa-plane fa-3x"} isHover={this.findWindowSize} name={"Travel"} description={"Travel and explore new places"}/>
            <HobbyCard Icon={"fas fa-palette fa-3x"} isHover={this.findWindowSize} name={"Art"} description={"A strong passion in creating art"}/>
        {/* </HobbyContainer> */}
        </Wrapper>
    )
    }
}


export {Hobby};

const Wrapper = styled.div`
display:flex;
flex-direction:row;
justify-content: center;

@media screen and (max-width: 420px) {
    display:flex;
    flex-direction:column;
    justify-content: center;
 }


`;

const Title = styled.h1``;
