import React, { Component } from "react";
import styled from "styled-components";
import { ContactCard } from "../share-components/ContactCard";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { FollowContainer } from "../component/FollowContainer/FollowContainer";
import axios from 'axios';

class Contact extends Component{

render(){
    return(
        <div>
            <TitleContainer>
                <Title>Contact</Title>
            </TitleContainer>


            <MainContainer>
                <Information>
                    <h2>Contact information</h2>
                    <ContactCard ><i className="fas fa-envelope"/> Vickelleung@gmail.com</ContactCard>
                    <ContactCard > <i className="fas fa-phone"/> 514-815-8638</ContactCard>
                    <ContactCard > <i className="fas fa-map-marker-alt"/> Montreal, Qc</ContactCard>
                    <Bar />
                    <SocialContainer>
                        <p>Follow me throught social media</p>
                        <FollowContainer />
                    </SocialContainer>
                </Information>

                <MessageBox>
                <form name="contact" netlify  method="POST" data-netlify="true">
                    <h2>Send me a message</h2>

                    <InputField
                        style={{margin: '5% 0'}}
                        id="input_name"
                        label="Name"
                        type="text"
                        variant="outlined"
                        ref="name"
                        // onChange={(e) => this.setState({ name: e.target.value })}
                    />
                     <InputField
                        style={{margin: '5% 0'}}
                        id="email"
                        label="Email"
                        type="email"
                        variant="outlined"
                        // onChange={(e) => this.setState({ email: e.target.value })}
                    />

                    <InputField
                        style={{margin: '5% 0', width:"100%"}}
                        id="message"
                        label="Write your message here"
                        multiline
                        rows="4"
                        variant="outlined"
                        // onChange={(e) => this.setState({ message: e.target.value })}
                    />
                    <div>
                        <SubmitBtn type="submit" variant="lined" >Submit</SubmitBtn>
                    </div>
                    </ form>
                </MessageBox>

                <form name="contact" method="POST" data-netlify="true">
                    <p>
                        <label>Your Name: <input type="text" name="name" /></label>   
                    </p>
                    <p>
                        <label>Your Email: <input type="email" name="email" /></label>
                    </p>
                    <p>
                        <label>Your Role: <select name="role[]" multiple>
                        <option value="leader">Leader</option>
                        <option value="follower">Follower</option>
                        </select></label>
                    </p>
                    <p>
                        <label>Message: <textarea name="message"></textarea></label>
                    </p>
                    <p>
                        <button type="submit">Send</button>
                    </p>
                    </form>
            </MainContainer>

        </div>
    )
 }
}

export {Contact};

const Information = styled.div`
text-align: left;
margin: 6% 6%;
padding: 3%;
border:2px solid black;
width:50%;

`;

const MessageBox = styled.div`
display:flex;
flex-direction:column;
margin: 6% 6%;
padding: 3%;
border:2px solid black;
width:50%;
`;

const MainContainer = styled.div`
display:flex;
flex-direction:row;
text-align:center;
height:100%;
width:100%;
`;

const TitleContainer = styled.div`
text-align:center `;

const Title = styled.h2``;

const InputField = styled(TextField)`
.MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline{
    border-color: #FDC500;

}

.MuiOutlinedInput-root.Mui-focused {
    color: black;
}

.MuiFormLabel-root.Mui-focused{
    color: #FDC500;
}

`;
// border-color: #FDC500;

const SubmitBtn = styled(Button)`
.MuiTouchRipple-root{
    color: #FDC500;
}

`;

const SocialContainer = styled.div`
display: flex;
flex-direction:column;
align-items: center;
justify-content: center;
`;

const Bar = styled.div`
margin: 8% 0;
border-bottom: 1px solid black;

`;

// setupProxy
