border-radius: 3%;

@media screen and (max-width: 420px) {
   width: 100%;
}
`;

const Header = styled.h1``;

const Information = styled.p`
text-align:left;
`;

const BulletPoints = styled.p`
text-align:justify;
`;

const FactBox = styled.div`
border:2px solid black;
padding:4%;
