import React from 'react';
// import Headroom from "react-headroom";
import scrollToComponent from 'react-scroll-to-component';

import {NavigationBar} from "./component/navigationBar/NavigationBar";
import { Homepage } from './sections/HomePage';
import { AboutPage } from './sections/AboutPage';
import styled from "styled-components";
import { Portfolio } from './sections/Portfolio';
import { Contact } from './sections/Contact';



class App extends React.Component {

render(){  


    return (
      <Wrapper>

        {/* <button onClick={() => scrollToComponent(this.contact, { offset: 0, align: 'bottom', duration: 500, ease:'inExpo'})} >test</button> */}
        {/* <Headroom> */}
          {/* <NavigationBar> */}
        {/* </Headroom> */}
        <Headroom>
          <div>
          <button onClick={() => scrollToComponent(this.home, { offset: 0, align: 'top', duration: 1500, ease:'inCirc'})}>Home</button>
          <button onClick={() => scrollToComponent(this.about, { offset: 0, align: 'top', duration: 1500, ease:'inCirc'})}>About Me</button>
          <button onClick={() => scrollToComponent(this.portfolio, { offset: 0, align: 'top', duration: 1500, ease:'inCirc'})}>Portfolio</button>
          <button onClick={() => scrollToComponent(this.contact, { offset: 0, align: 'top', duration: 1500, ease:'inCirc'})}>Contact Me</button>
          </div>
        </Headroom>

        <Homepage  ref={this.home}/>
        <AboutPage ref={this.about}/>
        <section ref={(section) => { this.Blue = section; }} >
         <Portfolio />
        </section>
        <Contact ref={(Contact)=> { this.contact = Contact; }}/>
        {/* <section className='blue' ref={(section) => { this.Blue = section; }}>Blue</section> */}
      </Wrapper>
    );
  }
}

export default App;

const Wrapper = styled.div`
width:auto;
min-width:50em;
`;
