                        label="Name"
                        type="text"
                        variant="outlined"
                    />
                     <InputField
                        style={{margin: '5% 0'}}
                        id="email"
                        label="Email"
                        type="email"
                        variant="outlined"
                    />

                    <InputField
                        style={{margin: '5% 0'}}
                        id="message"
                        label="Write your message here"
                        multiline
                        onChange={()=>this.changeInput()}
                        rows="4"
                        variant="outlined"
                    />


                    <SubmitBtn onClick={this.submitInfo} variant="lined" >Submit</SubmitBtn>
                </MessageBox>
            </MainContainer>

        </div>
    )
 }
}

