    return(
        <div>
            <TitleContainer>
                <Title>Contact</Title>
            </TitleContainer>


            <MainContainer>
                <Information>
                    <h2>Contact information</h2>
                    <ContactCard ><i className="fas fa-envelope"/> Vickelleung@gmail.com</ContactCard>
                    <ContactCard > <i className="fas fa-phone"/> 514-815-8638</ContactCard>
                    <ContactCard > <i className="fas fa-map-marker-alt"/> Montreal, Qc</ContactCard>
                    <Bar />
                    <SocialContainer>
                        <p>Follow me throught social media</p>
                        <FollowContainer />
                    </SocialContainer>
                </Information>

                <MessageBox>
                    <h2>Send me a message</h2>
                    <InputField
                        style={{margin: '5% 0'}}
                        id="input_name"
                        label="Name"
                        type="text"
                        variant="outlined"
                    />
                     <InputField
                        style={{margin: '5% 0'}}
                        id="email"
                        label="Email"
                        type="email"
                        variant="outlined"
                    />

                    <InputField
                        style={{margin: '5% 0'}}
                        id="message"
                        label="Write your message here"
                        multiline
                        // onChange={this.changeInput}
                        rows="4"
                        variant="outlined"
                    />


                    <SubmitBtn  variant="lined" >Submit</SubmitBtn>
                </MessageBox>
            </MainContainer>

        </div>
    )
 }
}

export {Contact};

