const Image = styled.img`
height:auto;
width:auto;
height:10em;
width:10em;
object-fit: cover;

`;

const LinkContainer = styled.div`
float:right;
`;

const InfoWrapper = styled.div`
flex: 0 0 22%;
margin: 4%;
display:flex;
