import React from 'react';
import Headroom from "react-headroom";
import scrollToComponent from 'react-scroll-to-component';

import {NavigationBar} from "./component/navigationBar/NavigationBar";
import { Homepage } from './sections/HomePage';
import { AboutPage } from './sections/AboutPage';
import styled from "styled-components";
import { Portfolio } from './sections/Portfolio';
import { Contact } from './sections/Contact';



class App extends React.Component {

render(){  
  this.componentDidMount=()=>{
    this.home =  React.createRef;
    this.about =  React.createRef;
    this.portfolio =  React.createRef;
    this.contact =  React.createRef;
  }

    return (
      <Wrapper>
        <button onClick={() => scrollToComponent(this.contact, { offset: 0, align: 'bottom', duration: 500, ease:'inExpo'})} >test</button>
        <Headroom>
          <NavigationBar/>
        </Headroom>

          <Homepage  ref={this.home}/>
        <AboutPage ref={this.about}/>
        <Portfolio ref={this.portfolio}/>
        <Contact ref={(div)=> { this.contact = div; }}/>
      </Wrapper>
    );
  }
}

export default App;

const Wrapper = styled.div`
width:auto;
min-width:50em;
`;
