import React from 'react';
import Headroom from "react-headroom";


import {NavigationBar} from "./component/navigationBar/NavigationBar";
import { Homepage } from './sections/HomePage';
import { AboutPage } from './sections/AboutPage';
import styled from "styled-components";
import { Portfolio } from './sections/Portfolio';
import { Contact } from './sections/Contact';



class App extends React.Component {

render(){  


    return (
      <Wrapper>

        {/* <button onClick={() => scrollToComponent(this.contact, { offset: 0, align: 'bottom', duration: 500, ease:'inExpo'})} >test</button> */}
        <Headroom>
          <NavigationBar/>
        </Headroom>

          <Homepage  ref={this.home}/>
        <AboutPage ref={this.about}/>
        <section ref={(section) => { this.Blue = section; }} >
         <Portfolio />
        </section>
        <Contact ref={(Contact)=> { this.contact = Contact; }}/>
        {/* <section className='blue' ref={(section) => { this.Blue = section; }}>Blue</section> */}
      </Wrapper>
    );
  }
}

export default App;

const Wrapper = styled.div`
width:auto;
min-width:50em;
`;
