import React from "react";
import styled from "styled-components";
import { Timeline } from "../component/timeline/Timeline";
import Button from '@material-ui/core/Button';

class AboutPage extends React.Component{
    render(){
        return(
            <Wrapper>
                <Title>About me</Title>
                <AboutContainer>
                    <MainContainer>
                        <GeneralContainer>
                            <InformationBox>
                                <Header>Hello! I'm Vickel</Header>
                                <Information>Thanks for reading my page. I am a young aspiring programmer that enjoy coding a lot!

                                </Information>
                            </InformationBox>
                            <Button variant="contained">Download Resume</Button>
                        </GeneralContainer>

                    </MainContainer>
                    <SecondContainer>
                        <FactBox>
                            <Header>Fun facts about me</Header>
                            <BulletPoints>1- I love food</BulletPoints>
                        </FactBox>
                    </SecondContainer>
                </AboutContainer>

                <TimeContainer>
                    <Timeline />
                </TimeContainer>
            </Wrapper>
        )
    }
}

export {AboutPage};

const Title = styled.h1`text-align:center;`;

const Wrapper = styled.div`
height:auto;
`

const MainContainer = styled.div`
    border-right:2px solid black;
    height:50%;
    display:relative;
    width:60%;
`;

const SecondContainer = styled.div`
width:40%;

`;

const DownloadButton = styled.button`
padding: 1.5%;
text-decoration:none;
color:black;
background-color: #C4C4C4;
`;

const InformationBox = styled.div`

border:2px solid black;
padding:4%;

`;

const Header = styled.h1``;

const Information = styled.p``;

const BulletPoints = styled.p`
text-align:justify;
`;

const FactBox = styled.div`
border:2px solid black;
padding:4%;
margin:0 8%;
text-align:center;
`;

const AboutContainer = styled.div`
display:flex; 
flex-direction:row;

width:100%;

margin-bottom:10%;
`;

const GeneralContainer = styled.div`


margin-left:40%;
margin-right:6%;
text-align:center;
`;

const TimeContainer = styled.div`
background-color:#434242;
`;
